    --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
    --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
    --shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
    --border-radius: 12px;
    --transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);

    --bg-gradient-start: #667eea;
    --bg-gradient-end: #764ba2;
    --card-bg: #FFFFFF;
    --text-primary: #1F2937;
    --text-secondary: #6B7280;
    --header-bg: #FFFFFF;
    --footer-bg: #1F2937;
    --footer-text: #F3F4F6;
}

body.dark-mode {
    --bg-gradient-start: #1a1a2e;
    --bg-gradient-end: #16213e;
    --card-bg: #0f3460;
    --text-primary: #e8e8e8;
    --text-secondary: #b0b0b0;
    --header-bg: #0f3460;
    --footer-bg: #0a1929;
    --footer-text: #e8e8e8;
    --light-gray: #1e3a5f;
    --white: #0f3460;
    --dark: #e8e8e8;
    --gray: #b0b0b0;
}

body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
    background: linear-gradient(135deg, var(--bg-gradient-start) 0%, var(--bg-gradient-end) 100%);
    min-height: 100vh;
    line-height: 1.6;
    color: var(--text-primary);
    overflow-x: hidden;
    transition: background 0.3s ease, color 0.3s ease;
    position: relative;
    width: 100%;
}

.modern-container {
    min-height: 100vh;
    max-width: 1400px;
    margin: 0 auto;
    overflow-x: hidden;
    position: relative;
    width: 100%;
}

/* Top Promo Bar */
.top-promo-bar {
    background: linear-gradient(90deg, var(--primary-color), var(--secondary-color));
    color: white;
    padding: 12px 20px;
    box-shadow: var(--shadow-md);
    position: relative;
    z-index: 999;
}

.promo-bar-content {
    max-width: 1200px;
    margin: 0 auto;
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 15px;
}

.promo-text {
    font-size: 14px;
    font-weight: 500;
}

.promo-bar-btn {
    background: white;
    color: var(--primary-color);
    padding: 6px 20px;
    border-radius: 6px;
    text-decoration: none;
    font-weight: 600;
    font-size: 13px;
    transition: var(--transition);
}

.promo-bar-btn:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-lg);
}

/* Modern Header */
.modern-header {
    background: var(--header-bg);
    box-shadow: var(--shadow-md);
    position: sticky;
    top: 0;
    z-index: 1000;
    transition: background 0.3s ease;
    width: 100%;
}

.header-content {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px 24px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 20px;
}

.logo-section {
    display: flex;
    align-items: center;
    gap: 15px;
}

.logo-icon {
    width: 50px;
    height: 50px;
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 24px;
    box-shadow: var(--shadow-md);
}

.logo-text h1 {
    font-size: 22px;
    font-weight: 800;
    color: var(--text-primary);
    margin: 0;
}

.logo-subtitle {
    font-size: 12px;
    color: var(--text-secondary);
    font-weight: 500;
}

.theme-toggle-btn {
    width: 44px;
    height: 44px;
    border-radius: 50%;
